import React, { useMemo, useState } from "react";
import styles from "./reserveBook.module.css";
import Navbar from "../../components/navbar/Navbar";
import { DateRange } from "react-date-range";
import Footer from "../../components/footer/Footer";
import { useNavigate, useLocation } from "react-router";

function ReserveBookPage() {
    const location = useLocation();
    const hotel = location.state;
    console.log(hotel);
    const [date, setDate] = useState([
        {
            startDate: new Date(),
            endDate: new Date(),
            key: "selection",
        },
    ]);

    // Get currentUser information:
    const curUser = useMemo(
        () => JSON.parse(localStorage.getItem("currentUser")),
        []
    );

    // States:
    const [userData, setUserData] = useState(curUser);

    // userData state Handler functions:
    function fullNameChange(e) {
        setUserData((data) => {
            return { ...data, fullName: e.target.value };
        });
        console.log(userData);
    }
    return (
        <div className={styles["reserve-book"]}>
            <Navbar />
            <div className={styles["reserve-container"]}>
                <div className={styles["top"]}>
                    <div className={styles["top-left"]}>
                        <h1>{hotel.name}</h1>
                        <p>{hotel.desc}</p>
                    </div>
                    <div className={styles["top-right"]}>
                        <div>
                            <span className={styles.price}>
                                ${hotel.cheapestPrice}
                            </span>{" "}
                            (1 night)
                        </div>
                        <button>Reserve or Book Now!</button>
                    </div>
                </div>

                {/* Form */}
                <div className={styles["reserve-form"]}>
                    <form action="#">
                        {/* date picker */}
                        <div className={styles["date-picker"]}>
                            <h2>Date</h2>
                            <DateRange
                                editableDateInputs={true}
                                onChange={(item) => setDate([item.selection])}
                                moveRangeOnFirstSelection={false}
                                ranges={date}
                                className={styles.date}
                                minDate={new Date()}
                            />
                        </div>

                        <div className={styles["user-form"]}>
                            <h2>Reserve Information</h2>
                            <div className={styles.controls}>
                                <label htmlFor="fullname">Your Full Name</label>
                                <input
                                    type="text"
                                    name="fullName"
                                    id="full-name"
                                    placeholder="Full Name"
                                    defaultValue={curUser.fullName}
                                    onChange={fullNameChange}
                                    // value={curUser.fullName}
                                />
                            </div>
                            <div className={styles.controls}>
                                <label htmlFor="email">Your Email</label>
                                <input
                                    type="email"
                                    name="email"
                                    id="email"
                                    placeholder="Email"
                                    defaultValue={curUser.email}
                                />
                            </div>
                            <div className={styles.controls}>
                                <label htmlFor="phone-number">
                                    Your Phone Number
                                </label>
                                <input
                                    type="text"
                                    name="phoneNumber"
                                    id="phone-number"
                                    placeholder="Phone Number"
                                    defaultValue={curUser.phoneNumber}
                                />
                            </div>
                            <div className={styles.controls}>
                                <label htmlFor="identity-card">
                                    Your Identity Card Number
                                </label>
                                <input
                                    type="text"
                                    name="identityCard"
                                    id="identity-card"
                                    placeholder="Card Number"
                                />
                            </div>
                        </div>
                    </form>
                </div>

                {/* Bottom */}
                <div className={styles["select-room"]}>
                    <h2>Select Rooms</h2>
                    <div className={styles["rooms-list"]}>
                        <div className={styles["room"]}>
                            <div className={styles["room-info"]}>
                                <h3 className={styles["room-title"]}>
                                    Lorem ipsum dolor sit.
                                </h3>
                                <p>Pay nothing until tomorrow</p>
                                <div className={styles["room-maxpp"]}>
                                    Max People: <span>2</span>
                                </div>
                                <div className={styles["room-price"]}>$350</div>
                            </div>
                            <div className={styles["room-choose"]}>
                                <div className={styles["room-checkbox"]}>
                                    <label htmlFor="101">101</label>
                                    <input type="checkbox" />
                                </div>

                                <div className={styles["room-checkbox"]}>
                                    <label htmlFor="101">101</label>
                                    <input type="checkbox" />
                                </div>

                                <div className={styles["room-checkbox"]}>
                                    <label htmlFor="101">101</label>
                                    <input type="checkbox" />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Total Bill */}
                <div className={styles["total-bill"]}>
                    Total Bill : <span>$700</span>
                </div>
                {/* Payment */}
                <div className={styles["payment"]}>
                    <form action="#">
                        <select name="payment" id="payment">
                            <option value="">Select Payment Method</option>
                            <option value="cash">Cash</option>
                            <option value="Credit Card">Credit Card</option>
                        </select>

                        <button>Reserve Now</button>
                    </form>
                </div>

                <Footer />
            </div>
        </div>
    );
}

export default ReserveBookPage;
