import "./searchItem.css";

const SearchItem = ({ data }) => {
    return (
        <div className="searchItem">
            <img src={data.photos[0]} alt="" className="siImg" />
            <div className="siDesc">
                <h1 className="siTitle">{data.name}m</h1>
                <span className="siDistance">{data.distance} from center</span>
                <span className="siTaxiOp">{data.featured}</span>
                <span className="siSubtitle">{data.description}</span>
                <span className="siFeatures">{data.type}</span>
                {/* If can cancel */}
                {data.featured ? (
                    <div>
                        <span className="siCancelOp">Free cancellation </span>
                        <span className="siCancelOpSubtitle">
                            You can cancel later, so lock in this great price
                            today!
                        </span>
                    </div>
                ) : (
                    <div></div>
                )}
            </div>
            <div className="siDetails">
                <div className="siRating">
                    <span>Excellent</span>
                    <button>{data.rating}</button>
                </div>
                <div className="siDetailTexts">
                    <span className="siPrice">${data.cheapestPrice}</span>
                    <span className="siTaxOp">Includes taxes and fees</span>
                    <button className="siCheckButton">See availability</button>
                </div>
            </div>
        </div>
    );
};

export default SearchItem;
