import { useState } from "react";
import "./navbar.css";
import styles from "./navbar.module.css";
import { useNavigate } from "react-router-dom";
const Navbar = () => {
    const navigate = useNavigate();
    const currentUser = JSON.parse(localStorage.getItem("currentUser"));
    const isLogin = currentUser ? true : false;
    const [loginState, setLoginState] = useState(isLogin);

    function toSignUp(event) {
        navigate("/sign-up");
    }

    function toSignIn(event) {
        navigate("/sign-in");
    }

    function toTransaction() {}

    function logOut() {
        localStorage.removeItem("currentUser");
        setLoginState(false);
    }
    return (
        <div className="navbar">
            <div className="navContainer">
                <span className="logo">Booking Website</span>
                {loginState ? (
                    <div className="navItems">
                        <p>{currentUser.userName}</p>
                        <button className="navButton" onClick={toTransaction}>
                            Transaction
                        </button>
                        <button className="navButton" onClick={logOut}>
                            Logout
                        </button>{" "}
                    </div>
                ) : (
                    <div className="navItems">
                        <button className="navButton" onClick={toSignUp}>
                            Register
                        </button>
                        <button className="navButton" onClick={toSignIn}>
                            Login
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Navbar;
